import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000',
});

// Envia uma mensagem
export const sendMessage = (token, receiver_id, content) =>
  api.post(
    '/messages',
    { receiver_id, content },
    { headers: { Authorization: `Bearer ${token}` } }
  );

// Busca a conversa entre o usu치rio logado e outro usu치rio
export const getConversation = (token, receiver_id) =>
  api.get(`/messages/${receiver_id}`, {
    headers: { Authorization: `Bearer ${token}` },
  });

// Busca os contatos do chat
export const getChatContacts = (token) =>
  api.get('/messages/contacts', {
    headers: { Authorization: `Bearer ${token}` },
  });

export default api;
